function handler_image_onClick(mouseev){
    app.jGrowl('Syncing data with server');	

// get all customers and save to local file system
    app.twirlyThing('pageSync', true);
    app.httpRequest(website+"/getalldata.php", "GET", function(data, error, httpResponse){

        driversArray = data;
        alert(app.OBJ2JSON(driversArray));
