        app.jGrowl('Data synced okay');
        newDriversArray =[];
        for (var i5 = 0; i5 < driversArray.length; i5++)

                {
//<p style="font-size:18px; color:#000000">     <strong><span style="font-family:arial,helvetica,sans-serif;"><span style="font-size:18px;">Home Page</span></span></strong><br /> 
